/* eslint-disable */

import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {
  let [modal, setModal] = useState(false)
  let [titles, setTitles] = useState(['Ïã†Î∞úÍ∞ÄÍ≤å', 'ÏïÑÏù¥Ïä§ÌÅ¨Î¶º Í∞ÄÍ≤å', 'Ïπ¥Ìéò', 'ÏûêÎèôÏ∞® ÌåêÎß§Ï†ê'])
  let [like, setLike] = useState([0, 0, 0, 0])
  let [name, setName] = useState(0)
  let [reviews, setReviews] = useState([['Ïã†Î∞úÏù¥ ÎßõÏûàÏñ¥Ïöî', new Date().toLocaleString()], ['ÏïÑÏù¥Ïä§ÌÅ¨Î¶ºÏù¥ Ïù¥ÎªêÏöî', new Date().toLocaleString()], ['Ïª§ÌîºÍ∞Ä ÏûòÎÇòÍ∞ÄÏöî', new Date().toLocaleString()], ['Ï∞®Í∞Ä Ìñ•Í∏ãÌï¥Ïöî', new Date().toLocaleString()]])

  return (
    <div className="App">
      <div className='black-nav'>
        <h4>ReactBlog</h4>
      </div>

      <span onClick={() => {
        const copy = [...titles]
        const shuffled = shuffleArray(copy)
        console.log(shuffled  )
        setTitles(shuffled)
      }}> üîÑ </span>

      <span onClick={() => {
        const copy = [...titles]
        copy.sort()
        setTitles(copy)
      }}> üìã </span>

      {
        titles.map((title, i) => {
          return (
            <div className='list' key={i}>
              <h4 onClick={() => {
                setModal(!modal)
                setName(i)
              }}> {title}
                <span onClick={(e) => {
                  e.stopPropagation()
                  let copy = [...like]
                  copy[i] += 1
                  setLike(copy)
                }}> üëç </span>
                {like[i]}
                <span> ‚ùå </span>
              </h4>
              <Today></Today>
            </div>
          )
        })
      }
      {
        modal == true ? 
        <Modal 
          titles={titles} 
          idx={name}
          reviews = {reviews}
          ></Modal> : null
      }

    </div>
  );
}

function Modal(props) {
  return (
    <div className='modal'>
      <h4>{props.titles[props.idx]}</h4>
      <p>{props.reviews[props.idx][0]}</p>
      <p>{props.reviews[props.idx][1]}</p>
    </div>
  )
}

const Today = () => {
  let now = new Date().toDateString()
  return (
    <p>{now}</p>
  )
}

function shuffleArray(array) {
  for (let i = array.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [array[i], array[j]] = [array[j], array[i]];
  }
  return array;
}


export default App;
